mapper.select:
        select * from (
			select 
				*,
				(select user_name from T_BASE_USER where id = SPEECH_USER_ID) as speech_user_name,
				ROW_NUMBER () OVER (ORDER BY ID DESC) AS RowNumber
			from T_REPORT_SHARE_SPEECH
			where SHARE_ID=#{share_id}
        )as tmp_t 

				where tmp_t.RowNumber >=#{minRow} and tmp_t.RowNumber <#{maxRow}

select * from (
	select t0.*,
		t2.case_his_id as case_his_id,
		t2.case_his_num as case_his_num,
		t2.case_symptom as case_symptom,
		t2.patient_birthday as patient_birthday,
		t2.patient_gender as patient_gender,
		t1.org_name as share_org_name,
		t4.USER_NAME as share_user_name,
		ROW_NUMBER () OVER (ORDER BY T0.SHARE_TIME DESC) AS RowNumber
	from T_REPORT_SHARE t0
		left join T_ORGANIZATION t1 on t0.SHARE_ORG_ID=t1.id
		left join T_REPORT t2 on t0.REPORT_ID=t2.id
		left join T_BASE_USER t4 on t0.SHARE_USER_ID = t4.id
	where 1=1

			and T0.REPORT_ID=#{report_id}


			and T0.SHARE_ORG_ID=#{share_org_id}


			and T0.id=#{id}


			and T0.SHARE_USER_ID=#{share_user_id}


			and T0.CLOSE_SHARE_USER_ID=#{close_share_user_id}


			and T1.ORG_NAME like '%'+#{share_org_name}+'%'


			and T2.case_symptom like '%'+#{case_symptom}+'%'


			and t2.case_his_num like '%'+#{case_his_num}+'%'


			and t2.CASE_HIS_ID =#{case_his_id}


			and t0.status =#{status}


			and T0.SHARE_TIME between #{start} and #{end}

)as tmp_t 

	where tmp_t.RowNumber >=#{minRow} and tmp_t.RowNumber <#{maxRow}

select count(TA.id) from T_REPORT_SHARE TA
	LEFT JOIN T_ORGANIZATION TB ON TA.SHARE_ORG_ID = TB.ID
	LEFT JOIN T_REPORT TC ON TA.REPORT_ID = TC.ID
WHERE 1=1

		and TA.report_id=#{report_id}


		and TA.SHARE_ORG_ID=#{share_org_id}


		and TA.id=#{id}


		and TA.SHARE_USER_ID=#{share_user_id}


		and TA.CLOSE_SHARE_USER_ID=#{close_share_user_id}


		and TB.ORG_NAME like '%'+#{share_org_name}+'%'


		and TC.case_symptom like '%'+#{case_symptom}+'%'


		and TC.case_his_num like #{case_his_num}+'%'


		and TC.CASE_HIS_ID =#{case_his_id}


		and TA.status =#{status}


		and TA.SHARE_TIME between #{start} and #{end}

select * from (
	select t0.*,
		t1.org_name as send_org_name,
		t2.USER_NAME as send_user_name,
		t3.org_name as recv_org_name,
		t4.USER_NAME as recv_user_name,
		ROW_NUMBER () OVER (ORDER BY t0.send_time) AS RowNumber
	from T_REPORT_MSG t0
		left join T_ORGANIZATION t1 on t0.send_org_id=t1.id
		left join T_BASE_USER t2 on t0.send_user_id=t2.id
		left join T_ORGANIZATION t3 on t0.recv_org_id = t3.id
		left join T_BASE_USER t4 on t0.recv_user_id = t4.id
	where 1=1

			and t0.id=#{id}


			and t0.report_id=#{report_id}


			and t0.send_org_id=#{send_org_id}


			and t0.send_user_id=#{send_user_id}


			and t0.recv_org_id=#{recv_org_id}


			and t0.recv_user_id=#{recv_user_id}


			and t0.status=#{status}

	)as tmp_t 

		where tmp_t.RowNumber >=#{minRow} and tmp_t.RowNumber <#{maxRow}

select count(1) from T_REPORT_MSG where 1=1

	and id=#{id}


	and report_id=#{report_id}


	and send_org_id=#{send_org_id}


	and send_user_id=#{send_user_id}


	and recv_org_id=#{recv_org_id}


	and recv_user_id=#{recv_user_id}


	and status=#{status}

SELECT COUNT(DISTINCT REPORT_ID)
	FROM T_REPORT_MSG 
	WHERE status=#{status} and recv_user_id=#{curr_user_id} and RECV_ORG_ID=#{curr_org_id} and type=#{type}

select * from ( select t0.*, t1.case_his_num as medical_his_num, t2.ORG_NAME as request_org_name, t3.ORG_NAME as diagnosis_org_name, t4.ORG_NAME as publish_org_name, t5.value as device_name, t6.value as part_name, t7.USER_NAME as request_user_name, t8.USER_NAME as accept_user_name, t9.USER_NAME as verify_user_name, ROW_NUMBER () OVER (ORDER BY t0.CREATE_TIME desc) AS RowNumber from T_REPORT t0 left join T_CASE_HISTORY t1 on t0.CASE_HIS_ID=t1.id LEFT JOIN T_DIAGNOSIS_V2 t11 on t0.diagnosis_id = t11.id left join T_ORGANIZATION t2 on t11.REQUEST_ORG_ID=t2.id left join T_ORGANIZATION t3 on t11.DIAGNOSIS_ORG_ID=t3.id left join T_ORGANIZATION t4 on t0.PUBLISH_REPORT_ORG_ID=t4.id left join T_DIC_VALUE t5 on t0.DICOM_IMG_DEVICE_TYPE_ID=t5.id left join T_BASE_USER t7 on t11.REQUEST_USER_ID=t7.id left join T_BASE_USER t8 on t11.ACCEPT_USER_ID=t8.id left join T_BASE_USER t9 on t11.VERIFY_USER_ID=t9.id where 1=1
and t2.ORG_NAME like '%'+#{request_org_name}+'%'
and t7.USER_NAME like '%'+#{request_user_name}+'%'
and t8.USER_NAME like '%'+#{accept_user_name}+'%'
and t9.USER_NAME like '%'+#{verify_user_name}+'%'
and t3.ORG_NAME like '%'+#{diagnosis_org_name}+'%'
and t4.ORG_NAME like '%'+#{publish_org_name}+'%'
and t0.case_his_num like '%'+#{medicalHisNum}+'%'
and t0.DICOM_IMG_DEVICE_TYPE_ID=#{device_type_id}
and t0.CREATE_TIME between #{start} and #{end}
and t0.F_O_M=#{fom}
) tmp_t where tmp_t.RowNumber>=#{minRow} and tmp_t.RowNumber < #{maxRow}

    select * 
    from (
		select t1.*,
			t2.CREATE_TIME as report_time,
			t2.CASE_HIS_NUM as case_his_num,
			(select ORG_NAME from T_ORGANIZATION where id = t3.REQUEST_ORG_ID) as request_org_name,
			(select ORG_NAME from T_ORGANIZATION where id = t3.DIAGNOSIS_ORG_ID) as diagnosis_org_name,
			(select value from T_DIC_VALUE where id = t2.DICOM_IMG_DEVICE_TYPE_ID) as device_name
			,ROW_NUMBER () OVER (ORDER BY t1.create_time DESC) AS RowNumber
    	from T_MOFIDY_REPORT_REQUEST t1
	    	left join T_REPORT t2 on t1.report_id=t2.id
	    	left join T_DIAGNOSIS_V2 t3 on t2.diagnosis_id = t3.id
    	where 1=1

	    	    and t1.id = #{id}


	    	    and t1.report_id=#{report_id}


	    	    and t1.request_user_id=#{request_user_id}


	    	    and t1.request_org_id=#{request_org_id}


	    	    and t1.response_user_id=#{response_user_id}


	    	    and t1.response_org_id=#{response_org_id}


	    	    and t1.status=#{status}

    ) as tmp_t 

			where tmp_t.RowNumber >=#{minRow} and tmp_t.RowNumber <#{maxRow}

select count(id)
from T_MOFIDY_REPORT_REQUEST 
where 1=1

	    and id = #{id}


	    and report_id=#{report_id}


	    and request_user_id=#{request_user_id}


	    and request_org_id=#{request_org_id}


	    and response_user_id=#{response_user_id}


	    and response_org_id=#{response_org_id}


	    and status=#{status}

select * from ( select t1.*, t2.REQUEST_ORG_ID as request_org_id, t2.DIAGNOSIS_ORG_ID as diagnosis_org_id, t2.about_case_ids as about_case_ids, t2.REQUEST_USER_ID as request_user_id, t3.ORG_NAME as request_org_name, t5.ORG_NAME as publish_report_org_name, t4.ORG_NAME as diagnosis_org_name, (select USER_NAME from T_BASE_USER where id = t1.PRINT_USER_ID) as print_user_name, t6.USER_NAME as publish_user_name, (select sign_file_id from T_DOCTOR_USER where id = t1.publish_user_id) as publish_user_sign_file_id, t7.USER_NAME as request_user_name, (select USER_NAME from T_BASE_USER where id = t2.ACCEPT_USER_ID) as accept_user_name, (select value from T_DIC_VALUE where id = t1.dicom_img_device_type_id) as device_type_name, t8.USER_NAME as duty_report_user_name, t9.USER_NAME as duty_verify_user_name, t10.sign_file_id as duty_verify_user_sign_file_id, t11.ORG_NAME as duty_org_name, t11.LOGO_FILE_ID as duty_org_logo_file_id, ROW_NUMBER () OVER (ORDER BY t1.create_time desc) AS RowNumber from T_REPORT t1 left join T_DIAGNOSIS_V2 t2 on t1.diagnosis_id = t2.id left join T_ORGANIZATION t3 on t2.REQUEST_ORG_ID = t3.id left join T_ORGANIZATION t4 on t2.DIAGNOSIS_ORG_ID = t4.id left join T_ORGANIZATION t5 on t1.PUBLISH_REPORT_ORG_ID = t5.id left join T_BASE_USER t6 on t1.publish_user_id = t6.id left join T_BASE_USER t7 on t2.REQUEST_USER_ID = t7.id left join T_BASE_USER t8 on t1.duty_report_user_id = t8.id left join T_BASE_USER t9 on t1.duty_verify_user_id = t9.id left join T_DOCTOR_USER t10 on t1.duty_verify_user_id = t10.id left join T_ORGANIZATION t11 on t1.duty_org_id = t11.id where 1=1
and t1.id = #{id}
and (t1.view_user_id>0 || t1.PRINT_TIMES>0)
and (t1.view_user_id is null or t1.view_user_id < =0) and (t1.PRINT_TIMES is null or t1.PRINT_TIMES < = 0)
and t1.diagnosis_id = #{request_id}
and t1.publish_user_id=#{publish_user_id}
and t1.CREATE_TIME between #{start} and #{end}
and t1.DICOM_IMG_DEVICE_TYPE_ID = #{dicom_img_device_type_id}
and t1.CASE_HIS_ID = #{case_his_id}
and t1.case_his_num like #{case_his_num}+'%'
and t1.patient_name like #{patient_name}+'%'
and t1.patient_gender = #{patient_gender}
and t2.DIAGNOSIS_PRODUCT_ID = #{diagnosis_product_id}
and t2.diagnosis_org_id = #{diagnosis_org_id}
and t2.request_org_id = #{request_org_id}
and t2.request_user_id = #{request_user_id}
and ((t1.PIC_OPINION like '%'+#{sick_key_words}+'%') or (t1.PIC_CONCLUSION like '%'+#{sick_key_words}+'%') or (t1.case_symptom like '%'+#{sick_key_words}+'%'))
and t1.F_O_M = #{f_o_m}
and t3.ORG_NAME like '%'+#{request_org_name}+'%'
and t4.ORG_NAME like '%'+#{diagnosis_org_name}+'%'
and t5.ORG_NAME like '%'+#{publish_report_org_name}+'%'
and t6.USER_NAME like '%'+#{publish_report_user_name}+'%'
and t7.USER_NAME like '%'+#{request_user_name}+'%'
and t1.type = #{type}
and t1.source_type = #{source_type}
) tmp_t
where tmp_t.RowNumber>=#{minRow} and tmp_t.RowNumber < #{maxRow}

select count(t1.id) 
from T_REPORT t1
	left join T_DIAGNOSIS_V2 t2 on t1.diagnosis_id = t2.id
	left join T_ORGANIZATION t3 on t2.REQUEST_ORG_ID = t3.id
	left join T_ORGANIZATION t4 on t2.DIAGNOSIS_ORG_ID = t4.id
	left join T_ORGANIZATION t5 on t1.PUBLISH_REPORT_ORG_ID = t5.id
	left join T_BASE_USER t6 on t1.publish_user_id = t6.id
	left join T_BASE_USER t7 on t2.REQUEST_USER_ID = t7.id
where 1=1 

	    and t1.id = #{id}




				and (t1.view_user_id>0 || t1.PRINT_TIMES>0)


				and (t1.view_user_id is null or t1.view_user_id < =0)
				and (t1.PRINT_TIMES is null or t1.PRINT_TIMES < = 0)




		and t1.diagnosis_id = #{request_id} 


	    and t1.publish_user_id=#{publish_user_id}


	    and t1.CREATE_TIME between #{start} and #{end}


	    and t1.DICOM_IMG_DEVICE_TYPE_ID = #{dicom_img_device_type_id}


	    and t1.CASE_HIS_ID = #{case_his_id}


	    and t1.case_his_num like #{case_his_num}+'%'


	    and t1.patient_name like #{patient_name}+'%'


	    and t1.patient_gender = #{patient_gender}


	    and t2.DIAGNOSIS_PRODUCT_ID = #{diagnosis_product_id}


	    and t2.diagnosis_org_id = #{diagnosis_org_id}


	    and t2.request_org_id = #{request_org_id}


	    and t2.request_user_id = #{request_user_id}


	    and ((t1.PIC_OPINION like '%'+#{sick_key_words}+'%') or 
	    	(t1.PIC_CONCLUSION like '%'+#{sick_key_words}+'%') or 
	    	(t1.case_symptom like '%'+#{sick_key_words}+'%'))


		and t1.F_O_M = #{f_o_m}


		and t3.ORG_NAME like '%'+#{request_org_name}+'%'


		and t4.ORG_NAME like '%'+#{diagnosis_org_name}+'%'


		and t5.ORG_NAME like '%'+#{publish_report_org_name}+'%'


		and t6.USER_NAME like '%'+#{publish_report_user_name}+'%'


		and t7.USER_NAME like '%'+#{request_user_name}+'%'


		and t1.type = #{type}


		and t1.source_type = #{source_type}
